# --- ADMIN CHECK ---
async def is_admin(chat_id, user_id):
    try:
        member = await app.get_chat_member(chat_id, user_id)
        return member.status in (ChatMemberStatus.ADMINISTRATOR, ChatMemberStatus.OWNER)
    except:
        return False

# --- SHAYARI TAG COMMAND ---
@app.on_message(filters.command(["shayari"], prefixes=["/", "@", "#"]))
async def shayari_tag_nobita(client, message: Message):
    chat_id = message.chat.id
    if message.chat.type == ChatType.PRIVATE:
        return await message.reply("р╣П с┤Ы╩Ь╔кs с┤Дс┤Пс┤Нс┤Нс┤А╔┤с┤Е с┤П╔┤╩Я╩П ╥Ус┤П╩А ╔в╩Ас┤Пс┤Ьс┤Шs.")

    if not await is_admin(chat_id, message.from_user.id):
        return await message.reply("р╣П ╩Пс┤Пс┤Ь с┤А╩Ас┤З ╔┤с┤Пс┤Ы с┤Ас┤Ес┤Н╔к╔┤ ╩Щс┤А╩Щ╩П.")

    if chat_id in spam_chats:
        return await message.reply("тЪая╕П **ЁЭЧзЁЭЧоЁЭЧ┤ЁЭЧ┤ЁЭЧ╢ЁЭЧ╗ЁЭЧ┤ ЁЭЧФЁЭЧ╣ЁЭЧ┐ЁЭЧ▓ЁЭЧоЁЭЧ▒ЁЭШЖ ЁЭЧеЁЭШВЁЭЧ╗ЁЭЧ╗ЁЭЧ╢ЁЭЧ╗ЁЭЧ┤!**")

    spam_chats.append(chat_id)
    
    try:
        async for usr in client.get_chat_members(chat_id):
            if chat_id not in spam_chats:
                break
            if usr.user.is_bot or usr.user.is_deleted:
                continue

            usrtxt = f"<a href='tg://user?id={usr.user.id}'>{usr.user.first_name}</a>"
            txt = f"{usrtxt}\n\n{random.choice(SHAYRI)}\n\nтЬи **ЁЭЧХЁЭШЖ: ЁЭЧбЁЭЧ╝ЁЭЧпЁЭЧ╢ЁЭШБЁЭЧо ЁЭЧаЁЭШВЁЭШАЁЭЧ╢ЁЭЧ░**"
            
            await client.send_message(chat_id, txt)
            await asyncio.sleep(3.8) # Anti-Flood
            
    except Exception as e:
        print(f"Error: {e}")
    finally:
        if chat_id in spam_chats:
            spam_chats.remove(chat_id)

# --- STOP COMMAND ---
@app.on_message(filters.command(["cancelshayari", "shayarioff", "shstop"]))
async def stop_shayari(client, message: Message):
    if not await is_admin(message.chat.id, message.from_user.id):
        return await message.reply("р╣П с┤П╔┤╩Я╩П с┤Ас┤Ес┤Н╔к╔┤s с┤Дс┤А╔┤ sс┤Ыс┤Пс┤Ш с┤Ы╩Ь╔кs.")
    
    if message.chat.id in spam_chats:
        spam_chats.remove(message.chat.id)
        return await message.reply("тЬЕ **ЁЭЧжЁЭЧ╡ЁЭЧоЁЭШЖЁЭЧоЁЭЧ┐ЁЭЧ╢ ЁЭЧзЁЭЧоЁЭЧ┤ЁЭЧ┤ЁЭЧ╢ЁЭЧ╗ЁЭЧ┤ ЁЭЧжЁЭШБЁЭЧ╝ЁЭЧ╜ЁЭЧ╜ЁЭЧ▓ЁЭЧ▒ ЁЭЧпЁЭШЖ ЁЭЧбЁЭЧ╝ЁЭЧпЁЭЧ╢ЁЭШБЁЭЧо!**")
    await message.reply("тЭМ **ЁЭЧбЁЭЧ╝ЁЭШБ ЁЭЧ┐ЁЭШВЁЭЧ╗ЁЭЧ╗ЁЭЧ╢ЁЭЧ╗ЁЭЧ┤.**")
          
